    CS_FileNameTemp += (*P_CS_Order);
    cout << "CFits::CalcFeatureOffsets: CS_FileNameTemp = " << CS_FileNameTemp << endl;
    CS_FileNameTemp += CString(".dat");
    cout << "CFits::CalcFeatureOffsets: CS_FileNameTemp = " << CS_FileNameTemp << endl;
