        (*P_OFS_Log) << "CFits::SlitFunc: for(m(=" << m << ")=0; m<NRows(=" << D_A2_ImM.rows() << "); m++): 5. OArr set to " << OArr << endl;//.transpose(secondDim,firstDim) << endl;
