        cout << "CFits::SlitFunc: I_IAperture_In = " << I_IAperture_In << ": for(m(=" << m << ")=0; m<NRows(=" << D_A2_ImM.rows() << "); m++): OArr(this->I_OverSample=" << this->I_OverSample << ", this->I_OverSample) set to " << OArr(this->I_OverSample, this->I_OverSample) << endl;
