        cout << "CFits::SlitFunc: I_IAperture_In = " << I_IAperture_In << ": for(m(=" << m << ")=0; m<NRows(=" << D_A2_ImM.rows() << "); m++): OLIndVecArr = " << OLIndVecArr << endl;
